import React, { useState, useEffect } from "react";
import OpenAI from "openai";
import "./glamyblog.css"

const openai = new OpenAI({
  apiKey: "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",
  dangerouslyAllowBrowser: true,
});

const glamyblog = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const completion = openai.chat.completions.create({
      model: "gpt-4o-mini",
      store: true,
      messages: [
        {"role": "user", "content": "write a haiku about ai"},
      ],
    });

    completion.then((result) => {
      setMessages([{ text: result.choices[0].message, sender: "bot" }]);
    });
  }, []);

  const handleSend = async () => {
    if (!input.trim()) return;
    setLoading(true);

    try {
      const response = await openai.chat.completions.create({
        model: "gpt-3.5-turbo",
        messages: [{ role: "user", content: input }],
        max_tokens: 100,
      });

      const botReply = response.choices[0].message.content;
      setMessages([...messages, { text: input, sender: "user" }, { text: botReply, sender: "bot" }]);
    } catch (error) {
      console.error("OpenAI Error:", error);
    }

    setLoading(false);
    setInput("");
  };

  return (
    <div>
      <h2>AI Chatbot</h2>
      <div>
        {messages.map((msg, i) => (
          <p key={i} className={msg.sender}>{msg.text}</p>
        ))}
      </div>
      <input
        type="text"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        placeholder="Ask something..."
      />
      <button onClick={handleSend} disabled={loading}>{loading ? "Thinking..." : "Send"}</button>
    </div>
  );
};

export default AIPage;
