import React, { useState } from "react";
// import { Configuration, OpenAIApi } from "openai";

const configuration = new Configuration({
  apiKey: process.env.REACT_APP_OPENAI_API_KEY, // API key from .env
});
const openai = new OpenAIApi(configuration);

const AIPage = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [loading, setLoading] = useState(false);

  const handleSend = async () => {
    if (!input.trim()) return;
    setLoading(true);

    try {
      const response = await openai.createChatCompletion({
        model: "gpt-3.5-turbo",
        messages: [{ role: "user", content: input }],
        max_tokens: 100,
      });

      const botReply = response.data.choices[0].message.content;
      setMessages([...messages, { text: input, sender: "user" }, { text: botReply, sender: "bot" }]);
    } catch (error) {
      console.error("OpenAI Error:", error);
    }

    setLoading(false);
    setInput("");
  };

  return (
    <div>
      <h2>AI Chatbot</h2>
      <div>
        {messages.map((msg, i) => (
          <p key={i} className={msg.sender}>{msg.text}</p>
        ))}
      </div>
      <input
        type="text"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        placeholder="Ask something..."
      />
      <button onClick={handleSend} disabled={loading}>{loading ? "Thinking..." : "Send"}</button>
    </div>
  );
};

export default AIPage;
