import "./glamyai.css";
import React, { useState } from "react";
import { motion } from "framer-motion";

const BotChat = () => {
  const [messages, setMessages] = useState([
    { text: "Hello! How can I help you?", sender: "bot" },
  ]);
  const [input, setInput] = useState("");

  const sendMessage = () => {
    if (!input.trim()) return;

    // User ka message add karein
    const userMessage = { text: input, sender: "user" };
    setMessages((prev) => [...prev, userMessage]); // Update state
    const userInput = input; // Preserve input before clearing

    setInput("");

    setTimeout(() => {
      let botReplyText = "I'm a bot! Your message was: " + userInput;
      if (userInput.toLowerCase().includes("hi") || userInput.toLowerCase().includes("hello")) {
        botReplyText = "Namaste sir";
      }
      const botReply = { text: botReplyText, sender: "bot" };
      setMessages((prev) => [...prev, botReply]);
    }, 1000);
  };

  return (
    <div className="chat-container">
      <div className="messages">
        {messages.map((msg, index) => (
          <motion.div
            key={index}
            initial={{ opacity: 0, x: msg.sender === "user" ? 50 : -50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.3 }}
            className={`message ${msg.sender}`}
          >
            {msg.text}
          </motion.div>
        ))}
      </div>
      <div className="input-container">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && sendMessage()}
          placeholder="Type a message..."
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
};

export default BotChat;
