import React, { useState } from "react";

import "./glamyblog.css"; // Custom CSS

// OpenAI API Setup
const configuration = new Configuration({
  apiKey: process.env.REACT_APP_OPENAI_API_KEY, 
});
const openai = new OpenAIApi(configuration);

const AIPage = () => {
  const [messages, setMessages] = useState([
    { text: "Hello! I'm your AI. Ask me anything.", sender: "bot" },
  ]);
  const [input, setInput] = useState("");
  const [loading, setLoading] = useState(false);

  const handleSend = async () => {
    if (!input.trim()) return;

    const userMessage = { text: input, sender: "user" };
    setMessages([...messages, userMessage]);
    setInput("");
    setLoading(true);

    try {
      const response = await openai.createChatCompletion({
        model: "gpt-3.5-turbo",
        messages: [{ role: "user", content: input }],
        max_tokens: 100,
      });

      const botReply = { text: response.data.choices[0].message.content, sender: "bot" };
      setMessages((prev) => [...prev, botReply]);
    } catch (error) {
      setMessages((prev) => [
        ...prev,
        { text: "Error: Failed to fetch AI response.", sender: "bot" },
      ]);
    }

    setLoading(false);
  };

  return (
    <div className="chat-container">
      <h2 className="chat-header">ðŸš€ AI Chat Assistant</h2>
      <div className="message-box">
        {messages.map((msg, index) => (
          <div key={index} className={`message ${msg.sender}`}>
            {msg.text}
          </div>
        ))}
        {loading && <div className="message bot">Typing...</div>}
      </div>
      <div className="input-box">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Ask something..."
        />
        <button onClick={handleSend} disabled={loading}>
          {loading ? "..." : "Send"}
        </button>
      </div>
    </div>
  );
};

export default AIPage;
