import React, { useEffect, useState } from "react";
import "./home.css";

const Home = () => {
  const [products, setProducts] = useState([]); // Array to hold all products
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch("http://localhost:4000/api/products")
      .then((response) => response.json())
      .then((data) => {
        console.log("Fetched Data:", data);
        if (Array.isArray(data) && data.length > 0 && data[0].products) {
          setProducts(data[0].products); // Set all products
        } else {
          console.error("No products found in the response");
        }
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching product data:", error);
        setLoading(false);
      });
  }, []);

  return (
    <div className="home-container">
      <h1 className="header-title">Recommended Products</h1>
      {loading ? (
        <p>Loading products...</p>
      ) : products.length > 0 ? (
        <div className="product-list">
          {products.map((product) => (
            <div key={product.id} className="product-card">
              <h2 className="product-name">{product.name}</h2>
              <p className="product-brand">Brand: {product.brand}</p>
              <p className="product-price">
                Price: <span className="final-price">${product.final_price}</span>
              </p>
            </div>
          ))}
        </div>
      ) : (
        <p>No products available</p>
      )}
    </div>
  );
};

export default Home;
