import React, { useRef } from "react"; // Fixed import statement
import "./Header.css";
import SearchIcon from '@mui/icons-material/Search';
import ClearIcon from '@mui/icons-material/Clear';

const Header = () => {
    const inputFieldRef = useRef(null);

    const toggleClearButton = () => {
        const inputField = inputFieldRef.current;
        const clearButton = document.getElementById('clearButton');

        if (inputField && clearButton) { // Added null check for inputField and clearButton
            clearButton.style.display = inputField.value.trim() !== '' ? 'flex' : 'none'; // Simplified the condition
            inputField.style.width = inputField.value.trim() !== '' ? 'calc(100% - 40px)' : '100%'; // Adjust width based on clear button visibility
        }
    };

    const clearInput = () => {
        const inputField = inputFieldRef.current;
        const clearButton = document.getElementById('clearButton');

        if (inputField && clearButton) { // Added null check for inputField and clearButton
            inputField.value = ''; // Clear the input field
            clearButton.style.display = 'none'; // Hide the button
            inputField.style.width = '100%'; // Reset width when input is cleared
            inputField.focus(); // Optionally return focus to the input field
        }
    };

    return (
        <div className="header-body">
            <div className="header-logo">Glamy</div>
            <div className="header-search-box">
                <div className="search-input">
                    <input
                        type="text"
                        className="search-input-field"
                        placeholder="Search for products..."
                        aria-label="Search products"
                        ref={inputFieldRef}
                        onInput={toggleClearButton}
                    />
                </div>
                <div className="search-icon"><SearchIcon className="cseachi-icon-cross"/></div>
                <button id="clearButton" className="clear-btn" aria-label="Clear search" onClick={clearInput} style={{ display: 'none' }}> {/* Added id and initial style */}
                    <ClearIcon />
                </button>
                <ul className="suggestions-list"></ul>
            </div>
            <div className="header-categories">
                <div className="header-category">Categories</div>
                <div className="header-category">Online auth</div>
                <div className="header-category">About us</div>
                <div className="header-category">Sell on Glamy</div>
                <div className="header-category">Blog</div>
            </div>
            <div className="header-profile">Profile</div>
            <div className="header-wishlist">Wishlist</div>
        </div>
    );
};

export default Header;
