const BotChat = () => {
  const [messages, setMessages] = useState([
    { text: "Hello! Click on a question below or type your own query.", sender: "bot" },
  ]);
  const [input, setInput] = useState("");
  const messagesEndRef = useRef(null);

  const sendMessage = (userInput) => {
    if (!userInput.trim()) return;

    // User ka message show kare
    const userMessage = { text: userInput, sender: "user" };
    setMessages((prev) => [...prev, userMessage]);

    // Check if question exists in predefined list
    const foundQuestion = questionsList.find(q => q.question.toLowerCase() === userInput.toLowerCase());
    const botReplyText = foundQuestion ? foundQuestion.answer : "I'm not sure about that. Can you rephrase?";

    setTimeout(() => {
      const botReply = { text: botReplyText, sender: "bot" };
      setMessages((prev) => [...prev, botReply]);
    }, 1000);
  };

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  return (
    <div className="chat-container">
      <div className="questions">
        <h3>Common Questions:</h3>
        {questionsList.slice(0, 2).map((q, index) => (
          <button key={index} onClick={(e) => { e.preventDefault(); sendMessage(q.question) }} className="question-btn">
            {q.question}
          </button>
        ))}
      </div>

      <div className="messages">
        {messages.map((msg, index) => (
          <motion.div
            key={index}
            initial={{ opacity: 0, x: msg.sender === "user" ? 50 : -50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.3 }}
            className={`message ${msg.sender}`}
          >
            {msg.text}
          </motion.div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      <div className="input-container">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && sendMessage(input)}
          placeholder="Type a message..."
        />
        <button onClick={() => sendMessage(input)}>Send</button>
      </div>
    </div>
  );
};

export default BotChat;
