import React, { useEffect, useState } from "react";
import "./home.css";

const Home = () => {
  const [products, setProducts] = useState([]); // Array to hold all products
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch("http://localhost:4000/api/products")
      .then((response) => response.json())
      .then((data) => {
        console.log("Fetched Data:", data);
        if (Array.isArray(data) && data.length > 0 && data[0].products) {
          setProducts(data[0].products); // Set all products
        } else {
          console.error("No products found in the response");
        }
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching product data:", error);
        setLoading(false);
      });
  }, []);

  return (
    <div className="home-container">
      <h1 className="header-title">Recommended Products</h1>
      {loading ? (
        <p>Loading products...</p>
      ) : products.length > 0 ? (
        <div className="product-list">
          {products.map((product) => (
            <div key={product.id} className="product-card">
              <img
                src={product.image}
                alt={product.name}
                className="product-image"
              />
              <h2 className="product-name">{product.name}</h2>
              <p className="product-brand">Brand: {product.brand}</p>
              <p className="product-price">
                Price: <span className="original-price">${product.price}</span>{" "}
                <span className="final-price">${product.final_price}</span>
              </p>
              <p className="product-discount">Discount: {product.discount}</p>
              <p className="product-rating">
                Rating: {product.rating} ‚≠ê ({product.reviews_count} reviews)
              </p>
              <p className="product-stock">
                {product.stock ? "In Stock" : "Out of Stock"}
              </p>
              <div className="product-specifications">
                <h3>Specifications:</h3>
                <ul>
                  <li>Color: {product.specifications?.color || "N/A"}</li>
                  <li>Size: {product.specifications?.size || "N/A"}</li>
                  <li>Material: {product.specifications?.material || "N/A"}</li>
                  <li>Warranty: {product.specifications?.warranty || "N/A"}</li>
                </ul>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p>No products available</p>
      )}
    </div>
  );
};

export default Home;
