import React, { useState } from "react";

const AIPage = () => {
  const [messages, setMessages] = useState([
    { text: "Hello! I'm your personal AI. How can I assist you?", sender: "bot" },
  ]);
  const [input, setInput] = useState("");

  const handleSend = () => {
    if (!input.trim()) return;

    const userMessage = { text: input, sender: "user" };
    setMessages([...messages, userMessage]);

    setTimeout(() => {
      const botReply = { text: "This is a sample AI response!", sender: "bot" };
      setMessages((prev) => [...prev, botReply]);
    }, 1000);

    setInput("");
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white flex flex-col items-center p-6">
      <h1 className="text-3xl font-bold mb-4">âš¡ Personal AI Chat</h1>
      <div className="w-full max-w-lg bg-gray-800 rounded-lg p-4 shadow-lg">
        <div className="h-96 overflow-y-auto p-2 space-y-2">
          {messages.map((msg, index) => (
            <div
              key={index}
              className={`p-3 rounded-lg ${
                msg.sender === "user" ? "bg-blue-600 ml-auto text-right" : "bg-gray-700"
              } max-w-xs`}
            >
              {msg.text}
            </div>
          ))}
        </div>
        <div className="flex mt-4">
          <input
            className="flex-1 p-2 text-black rounded-l-lg"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Type your message..."
          />
          <button
            onClick={handleSend}
            className="bg-blue-600 px-4 py-2 rounded-r-lg hover:bg-blue-700"
          >
            Send
          </button>
        </div>
      </div>
    </div>
  );
};

export default AIPage;
