import "./glamyai.css"
import React, { useState } from "react";
import { motion } from "framer-motion";

const BotChat = () => {
  const [messages, setMessages] = useState([
    { text: "Hello! How can I help you?", sender: "bot" }
  ]);
  const [input, setInput] = useState("");

  const sendMessage = () => {
    if (!input.trim()) return;

    const userMessage = { text: input, sender: "user" };
    setMessages((prev) => [...prev, userMessage]);
    setInput("");

    // Bot response after 1 second
    setTimeout(() => {
      let botReplyText = "I'm a bot! Your message was: " + input;
      if (input.toLowerCase().includes("hi") || input.toLowerCase().includes("hello")) {
        botReplyText = "Namaste sir";
      }
      // Add more bot capabilities here
      // ...
      const botReply = { text: botReplyText, sender: "bot" };
      setMessages((prev) => [...prev, botReply]);
    }, 1000);
  };

  return (
    <div className="chat-container" style={styles.container}>
      <div className="messages" style={styles.messages}>
        {messages.map((msg, index) => (
          <motion.div
            key={index}
            initial={{ opacity: 0, x: msg.sender === "user" ? 50 : -50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.3 }}
            style={{ ...styles.message, ...(msg.sender === "user" ? styles.user : styles.bot) }}
          >
            {msg.text}
          </motion.div>
        ))}
      </div>
      <div className="input-container" style={styles.inputContainer}>
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type a message..."
          style={styles.input}
        />
        <button onClick={sendMessage} style={styles.button}>Send</button>
      </div>
    </div>
  );
};

// Inline styles for quick styling
const styles = {
  container: { width: "300px", margin: "auto", border: "1px solid #ccc", borderRadius: "8px", padding: "10px" },
  messages: { maxHeight: "300px", overflowY: "auto", display: "flex", flexDirection: "column", gap: "10px" },
  message: { padding: "8px", borderRadius: "5px", maxWidth: "70%", wordWrap: "break-word" },
  user: { alignSelf: "flex-end", backgroundColor: "#007bff", color: "#fff" },
  bot: { alignSelf: "flex-start", backgroundColor: "#f1f1f1", color: "#000" },
  inputContainer: { display: "flex", marginTop: "10px" },
  input: { flex: 1, padding: "8px", borderRadius: "5px", border: "1px solid #ccc" },
  button: { marginLeft: "5px", padding: "8px", border: "none", backgroundColor: "#007bff", color: "white", cursor: "pointer" }
};

export default BotChat;
