import React, { useState } from 'react';
import { Widget } from 'react-chatbot-kit';


// Simple configuration for the chatbot
const config = {
  initialMessages: [
    {
      type: 'bot',
      text: 'Hello! How can I assist you today?',
    },
  ],
  botName: 'EcoBot',
  customStyles: {
    botMessageBox: {
      backgroundColor: '#00bcd4',
    },
    chatButton: {
      backgroundColor: '#00bcd4',
    },
  },
};

const ActionProvider = ({ createChatBotMessage, setState, children }) => {
  const handleProductQuery = () => {
    const productMessage = createChatBotMessage('You can browse sneakers on our website.');
    setState((prev) => ({
      ...prev,
      messages: [...prev.messages, productMessage],
    }));
  };

  return (
    <>
      <div>{children}</div>
      <button onClick={handleProductQuery}>Tell me about Sneakers</button>
    </>
  );
};

const MessageParser = ({ children, actions }) => {
  const parse = (message) => {
    if (message.toLowerCase().includes('sneaker')) {
      actions.handleProductQuery();
    }
  };

  return (
    <>
      <div>{children}</div>
      <button onClick={() => parse('Tell me about sneakers')}>Test Sneakers Query</button>
    </>
  );
};

const ChatbotPage = () => {
  return (
    <div style={{ position: 'relative', width: '100vw', height: '100vh' }}>
      <Widget
        actionProvider={ActionProvider}
        messageParser={MessageParser}
        config={config}
      />
    </div>
  );
};

export default ChatbotPage;
