import { React, useState } from "react";
import "./login.css";

import Logo from "../assets/images/app.png";
import VisibilityIcon from "@mui/icons-material/Visibility";
import VisibilityOffIcon from "@mui/icons-material/VisibilityOff";

const Login = ({ onVerify }) => {

  const [num1, setNum1] = useState(0);
  const [num2, setNum2] = useState(0);
  const [operator, setOperator] = useState("+");
  const [userInput, setUserInput] = useState("");
  const [message, setMessage] = useState("");

  const generateCaptcha = () => {
    const ops = ["+", "-", "*"];
    const op = ops[Math.floor(Math.random() * ops.length)];
    const n1 = Math.floor(Math.random() * 10) + 1;
    const n2 = Math.floor(Math.random() * 10) + 1;

    setNum1(n1);
    setNum2(n2);
    setOperator(op);
    setUserInput("");
    setMessage("");
};

// Check Captcha Answer
const validateCaptcha = () => {
    let correctAnswer = 0;
    switch (operator) {
        case "+":
            correctAnswer = num1 + num2;
            break;
        case "-":
            correctAnswer = num1 - num2;
            break;
        case "*":
            correctAnswer = num1 * num2;
            break;
        default:
            break;
    }

    if (parseInt(userInput) === correctAnswer) {
        setMessage("✅ Verified Successfully!");
        onVerify(true);
    } else {
        setMessage("❌ Incorrect, Try Again!");
        onVerify(false);
    }
};

// Generate new Captcha on component load
useEffect(() => {
    generateCaptcha();
}, []);




  const [showPassword, setshowPassword] = useState(false);
  return (
    <div className="loginContainer-parent-login">
      <div className="loginContainer-login">
        <div className="logo-login">
          <img
            src={Logo}
            alt=""
            style={{ width: "50px", height: "auto", borderRadius: "10px" }}
          ></img>
        </div>
        <div className="inputFields-login">
          <div className="inputFieldContainer">
            <input
              className="inputField-login"
              placeholder="Email or Phone Number"
            ></input>
          </div>
          <div className="passwordVisibilityToggle">
            <input
              type={showPassword ? "text" : "password"}
              className="inputField-login"
              placeholder="Password"
            ></input>

            {showPassword ? (
              <VisibilityOffIcon
                onClick={() => setshowPassword(false)}
                className="login-visibilityoffIcon"
              />
            ) : (
              <VisibilityIcon
                onClick={() => setshowPassword(true)}
                className="login-visibilityIcon"
              />
            )}
          </div>
        </div>
        <div className="keptcha-login">keptcha</div>
        <div className="loginButton-login">login</div>
        <div className="forgotSignUp-login">
          <p className="forgotPassword-login">Forgot Password?</p>
          <p className="signUp-login">Sign Up</p>
        </div>
        <div className="googleLogin-login">Google</div>
      </div>
    </div>
  );
};

export default Login;
