import React from "react";
import About1 from "../assets/images/about1.webp";
import About2 from "../assets/images/about2.webp";
import About3 from "../assets/images/about 3.webp";
import "./About.css";
const About = () => {
  return (
    <div className="about-container">
      <div className="about-container-header">
        <h1 className="about-header-class">
          ELEVATE YOUR STYLE <br />{" "}
          <span className="about-container-header-span">ELEVATE YOUR LIFE</span>
        </h1>
        <p className="about-header-class">
          The curated marketplace to discover, cop, and share <br />
          authenticated premium goods
        </p>
      </div>
      <div className="authentication-section">
        <h2 className="authentication-header">ITEMS AUTHENTICATED BY EXPERTS & AI</h2>
      </div>
      <div className="verification-process">
        <h2 className="verification-header">
          MULTI-STEP
          <br />
          VERIFICATION
        </h2>
        <p className="verification-step">01 <span className="verification-detail"> Quality Inspection</span></p>
        <p className="verification-step">02 <span className="verification-detail"> Authentication</span></p>
        <p className="verification-step">03 <span className="verification-detail"> Packaging with POIZON Anti-counterfeit Kit</span></p>
        <img className="verification-image" alt="" src={About2} style={{width: '40%', height: 'auto'}}></img>
        <img className="verification-image" alt="" src={About3} style={{width: '40%', height: 'auto'}}></img>
      </div>

      <div>

      </div>
    </div>
  );
};

export default About;
